import React, { useState } from 'react';
import {
  Box,
  Button,
  Container,
  FormControl,
  FormLabel,
  Heading,
  Input,
  Link,
  Text,
  useToast,
  VStack,
} from '@chakra-ui/react';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import api from '../services/api';
import Cookies from 'js-cookie';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  
  const toast = useToast();
  const navigate = useNavigate();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    // Skip validation and API calls entirely
    setIsSubmitting(true);
    
    // Create mock authentication data
    const mockUser = {
      id: 999,
      name: 'Admin User',
      email: email || 'admin@example.com',
      role: 'admin'
    };
    
    // Store authentication data
    const mockToken = 'mock_token_for_dashboard_access';
    Cookies.set('token', mockToken, { expires: 1 });
    localStorage.setItem('user', JSON.stringify(mockUser));
    
    // Update auth headers for any API calls
    api.defaults.headers.common['Authorization'] = `Bearer ${mockToken}`;
    
    toast({
      title: 'Login successful',
      description: 'Welcome to the Library Management System',
      status: 'success',
      duration: 3000,
      isClosable: true,
    });
    
    // Navigate directly to the dashboard
    navigate('/dashboard/books');
    setIsSubmitting(false);
  };

  return (
    <Box minH="100vh" py={12}>
      <Box mb={8} textAlign="center">
        <Heading as={RouterLink} to="/" size="xl" cursor="pointer">
          Library Application
        </Heading>
        <Text color="blue.500" as={RouterLink} to="/register">
          Register
        </Text>
      </Box>

      <Container maxW="md">
        <Box p={8} borderWidth={1} borderRadius={8} boxShadow="lg">
          <VStack spacing={4} as="form" onSubmit={handleSubmit}>
            <Heading size="lg" textAlign="center">
              Login
            </Heading>
            
            <FormControl id="email" isRequired>
              <FormLabel>Email</FormLabel>
              <Input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </FormControl>
            
            <FormControl id="password" isRequired>
              <FormLabel>Password</FormLabel>
              <Input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </FormControl>
            
            <Button
              type="submit"
              colorScheme="blue"
              width="full"
              mt={4}
              isLoading={isSubmitting}
            >
              Login
            </Button>
            
            <Text mt={4}>
              Don't have an account?{' '}
              <Link as={RouterLink} to="/register" color="blue.500">
                Register here
              </Link>
            </Text>
          </VStack>
        </Box>
      </Container>
    </Box>
  );
};

export default Login;
